import React, { Component } from 'react';
import API from "./utils/API"
import EmployeeCard from './components/Card/Card';
import Wrapper from "../src/components/Wrapper/index";
import SearchForm from "../src/components/SearchForm/Search";



class App extends Component {
  state = {
    users: [],
    search: "",
    searchedUsers: []
  };

  componentDidMount() {
    API.getRandomEmployees().then(results => {
      this.setState({
        users: results.data.results,
      });
    });
  };

  handleInputChange = event => {
    this.setState({
      search: event.target.value
    });
  }

  handleFormSubmit = event => {
    event.preventDefault();
    console.log(this.state.search);
    let search = this.state.search;

    let searchedUsers = this.state.users.filter(item => {
      let values = Object.value(item).toLowerCase();
      return values.indexOf(search.join("").toLowerCase);
    })
    this.setState({
      searchedUsers: searchedUsers
    })
  }

  render () {
    return (
      <div>
        <SearchForm 
          formSubmit={this.handleFormSubmit}
          inputChange={this.handleInputChange}
          />
        <Wrapper>
          <React.Fragment>
          {this.state.users.map(user => (
            <EmployeeCard 
              first={user.name.first}
              last={user.name.last}
              image={user.picture.medium}
              dob={user.dob.age}
              phone={user.phone}
              email={user.email}
            />
          ))}

          </React.Fragment>
        </Wrapper>
      </div>
    )

    }
  }




export default App;
