import React, { Component } from 'react';
import './App.css';
import API from "./utils/API"



class App extends Component {
  state = {
    users: [{}]
  };
  componentDidMount() {
    API.getRandomEmployees()
      .then(res => this.setState({
        users: res.data.results
      })
      )
      .catch(err => console.log(err));
  }


}

export default App;
